alternatives.tex-math:
\documentclass[12pt]{minimal}
\usepackage{wasysym}
\usepackage[substack]{amsmath}
\usepackage{amsfonts}
\usepackage{amssymb}
\usepackage{amsbsy}
\usepackage[mathscr]{eucal}
\usepackage{mathrsfs}
\DeclareFontFamily{T1}{linotext}{}
\DeclareFontShape{T1}{linotext}{m}{n} {linotext }{}
\DeclareSymbolFont{linotext}{T1}{linotext}{m}{n}
\DeclareSymbolFontAlphabet{\mathLINOTEXT}{linotext}
\begin{document}
$$\eqalign{& {\rm{\% Wound \ contraction }} \cr & {\rm{ = }}{{{\rm{Wound \ area (day 0) - Wound \ area (day n) x 100}}} \over {{\rm{Wound \ area (day 0)}}}} \cr} $$
\end{document}

\documentclass[12pt]{minimal}
\usepackage{wasysym}
\usepackage[substack]{amsmath}
\usepackage{amsfonts}
\usepackage{amssymb}
\usepackage{amsbsy}
\usepackage[mathscr]{eucal}
\usepackage{mathrsfs}
\DeclareFontFamily{T1}{linotext}{}
\DeclareFontShape{T1}{linotext}{m}{n} {linotext }{}
\DeclareSymbolFont{linotext}{T1}{linotext}{m}{n}
\DeclareSymbolFontAlphabet{\mathLINOTEXT}{linotext}
\begin{document}
$$\eqalign{& {\rm{Percent \ Tensile \ Strength \ (TS) \ of \ Treated \ Group }} \cr & {\rm{ = }}{{{\rm{ TS \ of \ treated \ group - TS \ simple \ ointment x 100}}} \over {{\rm{TS \ simple \ ointment}}}} \cr} $$
\end{document}
